<powershell># written for windows server 2012 base on awsImport-Module ServerManager# Install IIS with standard featuresInstall-WindowsFeature -Name Web-Server# Adjust windows update settings to allow updates $AUSettings = (New-Object -com "Microsoft.Update.AutoUpdate").Settings$AUSettings.IncludeRecommendedUpdates = "True"$AUSettings.NotificationLevel = 4$AUSettings.Save()# detect and then install updatesC:\windows\system32\wuauclt.exe /detectnowStart-Sleep -s 15C:\windows\system32\wuauclt.exe /updatenowStart-Sleep -s 15# create default.htm file in proper placeNew-Item C:\inetpub\wwwroot\default.htm -type fileAdd-Content C:\inetpub\wwwroot\default.htm "<!DOCTYPE html>`n<html>`n<body>`n<h1>I Am Bootstrapped</h1>`n<h3>See how easy this is on Windows?</h3>`n</body>`n</html>`n"</powershell>